
import { Component, NgModule, ModuleWithProviders } from '@angular/core';

@Component({
    //moduleId: String(module.id),
    selector: 'mc-button',
    //styleUrls: ['src/lib/button/button.scss'],
    styles: [require('./button.scss')],
    template: `<button><ng-content></ng-content></button>`
})
    
export class Button {
    message = "Click Me...";

    onClick() {
        this.message = "Hello World! I'm clicked";
    }
}

@NgModule({
    exports: [Button],
    declarations: [Button]
})

export class ButtonModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: ButtonModule,
            providers: []
        };
  }
}