import {NgModule, ModuleWithProviders} from '@angular/core';

import { HelloWorldModule } from './hello-world/index';
import { ButtonModule } from './button/index';



const MEDRIO_MODULES = [
    HelloWorldModule,
    ButtonModule
];

@NgModule({
  imports: [
      HelloWorldModule.forRoot(),
        ButtonModule.forRoot()  
  ],
  exports: MEDRIO_MODULES,
})
    
export class MedrioRootModule { }


@NgModule({
  imports: MEDRIO_MODULES,
  exports: MEDRIO_MODULES,
})
    
export class MedrioModule {
  static forRoot(): ModuleWithProviders {
    return {ngModule: MedrioRootModule};
  }
}
